  filterCoaches() {
    this.filteredCoaches = this.coaches.filter(coach =>
      coach.user.name.toLowerCase().includes(this.searchText.toLowerCase()) ||
      coach.specialty.toLowerCase().includes(this.searchText.toLowerCase()) ||
      coach.user.address.toLowerCase().includes(this.searchText.toLowerCase()) ||
      coach.user.country.toLowerCase().includes(this.searchText.toLowerCase())
    );
  }

  selectCoach(coach: Coach) {
    const dialogRef = this.dialog.open(ContactDialogComponent, {
      width: '500px',
      data: { nombre: coach.user.name + ' ' + coach.user.lastname, coachId: coach.id }
    });

    dialogRef.afterClosed().subscribe(result => {
      if (result?.event === 'confirm') {
        this.requestService.contactCoach(result.coachId, result.memberId, result.message).subscribe(response => {
          console.log('Contacto confirmado con el coach:', coach.user.name + ' ' + coach.user.lastname);
        });
      }
    });
  }
